body.script:
        const mostrar = document.querySelector('#mostrar');
        const cerrar = document.querySelector('#cerrar');

        const localConfig = localStorage.getItem('tema');

        if (localConfig === 'mostrar') {

                div = document.getElementById('capi');
                div.style.display = '';
                a = document.getElementById('cerrar');
                a.style.display = '';
                a = document.getElementById('mostrar');
                a.style.display = 'none';
        }else if(localConfig === 'cerrar'){
                div = document.getElementById('capi');
                div.style.display = 'none';
                  a = document.getElementById('cerrar');
                a.style.display = 'none';
                a = document.getElementById('mostrar');
                a.style.display = '';
        }

        mostrar.addEventListener('click', () => {
            let acc;


                div = document.getElementById('capi');
                div.style.display = '';
                a = document.getElementById('cerrar');
                a.style.display = '';
                a = document.getElementById('mostrar');
                a.style.display = 'none';


            acc = document.querySelector('#mostrar') ? 'mostrar' : 'cerrar';
        
        localStorage.setItem('tema', acc)

        });

       
        cerrar.addEventListener('click', () => {
            let acc;
            
            acc = document.querySelector('#cerrar') ? 'cerrar' : 'mostrar';
            
            div = document.getElementById('capi');
            div.style.display = 'none';
              a = document.getElementById('cerrar');
            a.style.display = 'none';
            a = document.getElementById('mostrar');
            a.style.display = '';


        localStorage.setItem('tema', acc)
    });

  	const btnn = document.querySelector('#boton-theme');

const configUserbtn = window.matchMedia('prefers-color-scheme: dark');

const localConfigbtn = localStorage.getItem('temabtn');

if (localConfigbtn === 'dark') {
	document.body.classList.toggle('otro-theme')
}else if (localConfigbtn === 'light') {
	document.body.classList.toggle('otro-theme-claro')
}

btnn.addEventListener('click', () => {
	let colorTema;

	if (configUserbtn.matches) {
		document.body.classList.toggle('otro-theme-claro')

		colorTema = document.body.classList.contains('otro-theme-claro') ? 'light' : 'dark'
	}else{
		document.body.classList.toggle('otro-theme')
		colorTema = document.body.classList.contains('otro-theme') ? 'dark' : 'light'
	}
	localStorage.setItem('temabtn', colorTema)
})

