var disqus_shortname = "",
    disqus_config = function(){
       this.page.url = "";
       this.page.title = "";
    };//
 (function() {
     var d = document, s = d.createElement('script');
     s.async = true;s.src = '//' + disqus_shortname + '.disqus.com/embed.js';
     s.setAttribute('data-timestamp', +new Date());(d.head || d.body).appendChild(s);
 })();//

   /*!
    * wjs-related v2.1.0
    * Entradas relacionadas
    * Copyright 2018 zkreations
    * Developed by José Gregorio (fb.com/JGMateran)
    * Licensed under MIT (github.com/zkreations/wjs-related/blob/master/LICENSE)
    */
   var related = (function(){
   'use strict';

   //Configuracion
   var defaults = {
      id: '',
      homepage: window.location.protocol + '//' + window.location.hostname,
      image: 'src/imgdefault.jpg', //Imagen por defecto
      length: 4, //Cantidad de entradas
      localeDate: 'es-ES', //Idioma
      snippet: 50, //Cantidad de texto
      imgSize: 's256', //Recorte de la imagen
      container: document.getElementById('grid-movie-relacionadas'),
      tags: ['',]
   };

   //
   var tags$length = defaults.tags.length;
   var script = document.createElement( 'script' );
   var src = defaults.homepage + '/feeds/posts/default' +
      '?alt=json-in-script' +
      '&callback=related' +
      '&max-results=' + ( defaults.length + 1 ) +
      '&q=';
   for ( var n = 0; n < tags$length; n++ ){
      src += 'label:"' + defaults.tags[ n ] + '"' + ( n === tags$length - 1 ? '' : '|' );
   }
   script.src = src;
   document.body.appendChild( script );
   function render( data ){
      var title = data.title.$t;
      var content = data.content;
      var summary = data.summary;
      var body = content ? content.$t : summary.$t;
      var snippet = (body).replace(/<[^>]*>?/g,'').substring( 0, defaults.snippet ) + '...';
      var img = data.media$thumbnail;
      var tempHtml = document.createElement('div');
      tempHtml.innerHTML = body;
      var imgHtml = tempHtml.querySelector('img');
      var image = ( img ? img.url : (imgHtml ? imgHtml.src : defaults.image)).replace(/[swh]\d{2,4}(?:-[swh]\d{2,4})?(?:-c)?/, defaults.imgSize); 
      var url = (function(){
         for ( var i = 0; i < data.link.length; i++ ){
            var link = data.link[i];
            if ( link.rel === 'alternate' ){
               return link.href;
            }
         }
      })();
      var published = new Date( data.published.$t ).toLocaleDateString(
         defaults.localeDate,
         {year:'numeric', month:'long', day: 'numeric'}
      );
      return (
         '<div class="cards-movie">'+
            '<div class="movie-img">'+
               '<img src="' + image + '" alt="' + title + '" />'+
        '<i class="fa fa-play-circle" aria-hidden="true"></i>'+
                  '<a aria-label="Entradas-populares" href="' + url + '"></a>'+
       '</div>'+
             '<div class="movie-titulo">'+
          '<a href="' + url + '">'+
                      '<h3>' + title + '</h3>'+
             '</a>'+
               '</div>'+
         '</div>'
      );
   }
   function related( json ){
      var i = 0;
      var post;
      var length = defaults.length;
      for ( ; i < length && ( post = json.feed.entry[ i ] ); i++ ){
         if ( defaults.id !== post.id.$t.split( '.post-' )[ 1 ] ){
            defaults.container.innerHTML += render( post );
         } else {
            length++;
         }
      }
   }
   return related;
   })();
